body.script:
const API_KEY = 'AIzaSyBtRey4UY_MKDI2eKWdJhG3xZDPhXvdjpU';

async function loadYTM() {
    const response = await fetch(`https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=50&q=music&key=${API_KEY}`);
    const data = await response.json();
    return data.items;
}

function initPlayer(videoId) {
    return new YT.Player('player', {
        height: '200',
        width: '400',
        videoId: videoId,
        playerVars: {
            'autoplay': 1,
            'controls': 0,
            'disablekb': 1
        }
    });
}

window.onYouTubeIframeAPIReady = async () => {
    const tracks = await loadYTM();
    let currentTrack = 0;
    const player = initPlayer(tracks[currentTrack].id.videoId);

    document.getElementById('play').addEventListener('click', () => {
        player.getPlayerState() === 1 ? player.pauseVideo() : player.playVideo();
    });
};
